import { initializeApollo } from '../../api'
import { useQuery, gql } from '@apollo/client'

const querySchema = gql`
  query getOrganization($slug: String) {
    getOrganization(slug: $slug) {
      id
      name
      description
      logoUrl
      howItIsUsingDonations
      contactEmail
      contactPhone
      contactWebsite
      whatsappPhone
      facebookUrl
      twitterUrl
      instagramUrl
      donationBankAccountName
      donationLinks
      city
      country
      coordenateX
      coordenateY
      causes {
        name
      }
    }
  }
`

