export const getServerSideProps: GetServerSideProps = async ({ params }) => {
  const apolloClient = initializeApollo()

  const data = await apolloClient.query({
    query: querySchema,
    variables: { slug: params.slug },
  })
  apolloClient.cache.extract()
  return {
    props: {
      data,
    },
  }
}

export default ORG
