import { useState, useEffect } from 'react'
import GoogleMapReact from 'google-map-react'

import style from '../style.module.scss'

interface locationI {
  coordenates: {
    coordenateX: string
    coordenateY: string
  }
  city: string
  country: string
}

function Map(props: { location: locationI }) {
  const [coordenates, setCoordenates] = useState({
    y: -26.231718,
    x: -63.2485696,
  })
  console.log(location)
  const [zoom, setZoom] = useState(1)
  useEffect(() => {
    if (location.coordenates) {
      setCoordenates({
        x: location.coordenates.x,
        y: location.coordenates.y,
      })
      setZoom(12)
    }
  }, [location])

  return (
    <div className={style.map}>
      <GoogleMapReact
        bootstrapURLKeys={{
          key: 'AIzaSyAQyrX0Pjjnrlz--nYA-UapCAragOrRdtw',
        }}
        defaultCenter={{ lat: coordenates.y, lng: coordenates.x }}
        defaultZoom={zoom}
        options={{ styles: styles }}
      >
        {location && (
          <Marcador
            location={location}
            lat={coordenates.y}
            lng={coordenates.x}
            name="My Marker"
            color="blue"
          />
        )}
      </GoogleMapReact>
    </div>
  )
}

export default Map

export function Marcador(props: any) {
  const { color, name, location } = props

  return (
    <>
      <div
        className={style.marker}
        style={{ backgroundColor: color, cursor: 'pointer' }}
        title={name}
      />
      <div className={style.mapInfo}>
        <div className={style.marker}>
          <div className={style.markerFill}></div>
          <div className={style.markerFillFill}></div>
        </div>
        <div className={style.address}>
          <div className={style.textContainer}>
            <div>{location && `${location.city}, ${location.country}`}</div>
          </div>
        </div>
      </div>
    </>
  )
}

