import { useState, useEffect } from 'react'
import * as styleSass from '../../../styles/buttons.module.scss'

function Button(props) {
  const [rounded, setRounded] = useState({
    value: false,
    round: '10px',
  })

  useEffect(() => {
    if (props.rounded) {
      setRounded({ value: true, round: '50px' })
    } else {
      setRounded({ value: false, round: '10px' })
    }
  }, [props.rounded])
  const conStyle = {
    width: props.width,
    height: props.height,
    rounded: rounded,
  }
  return (
    <button style={conStyle} className={styleSass.button}>
      {props.children}
    </button>
  )
}
export default Button
