import { Layout, Modal } from 'antd'
import React, { useState, useEffect } from 'react'
import Link from 'next/link'
import { ModalContent } from './Contenido'

import TargetBase from '../usables/TargetBase'
import ResultItem from './ResultItem'
import * as styles from '../../styles/onglist.module.scss'

const { Sider } = Layout

function ContenidoSider(props: any) {
  const [estado, setEstado] = useState({
    visible: false,
  })
  const filter = props.filter
  const handleModal = () => {
    setEstado({ visible: true })
  }
  const handleClose = () => {
    setEstado({ visible: false })
  }
  const { ONGs } = props
  return (
    <Sider className={styles.ongListResult}>
      <div className={styles.ongListResultQuantity}>
        <h6>Mostrando {ONGs.data.length} proyectos</h6>
        <div className={styles.quantityBtn} onClick={handleModal}>
          FILTROS
        </div>
        <Modal
          maskClosable={true}
          footer={null}
          centered={true}
          onCancel={handleClose}
          visible={estado.visible}
        >
          <ModalContent />
        </Modal>
      </div>
      {filter && <TargetBase filter={filter} />}
      {ONGs.data.map((ong: any) => {
        return (
          <Link key={ong.slug} href={`/org/${ong.slug}`}>
            <a>
              <ResultItem
                name={ong.name}
                desc={ong.description}
                logo={ong.logo}
                city={ong.city}
                country={ong.country}
              />
            </a>
          </Link>
        )
      })}
    </Sider>
  )
}

function LazyContenidoSider({ ONGs, filter, select }) {
  const [show, setShow] = useState(false)

  useEffect(() => {
    const onChange = (entries) => {
      const el = entries[5]
      if (el.isIntersecting) {
        setShow(true)
      }
    }
    const observer = new IntersectionObserver(onChange, {
      rootMargin: '500px',
    })
    observer.observe(document.getElementById('LazyLoading'))
  })

  return (
    <div id="LazyLoading">
      {show ? (
        <ContenidoSider ONGs={ONGs} filter={filter} select={select} />
      ) : null}
    </div>
  )
}

export default LazyContenidoSider
