export default function Checker() {
  const [check, setCheck] = useState({
    checkedA: true,
    checkedB: true,
    checkedC: true,
  })

  const handleChange = (event) => {
    setCheck({ ...check, [event.target.name]: event.target.checked })
  }

  return (
    <FormGroup>
      <FormControlLabel
        control={
          <IOSSwitch
            checked={state.checkedB}
            onChange={handleChange}
            name="checkedB"
          />
        }
        label="iOS style"
      />
    </FormGroup>
  )
}
