import React, { useState, useEffect } from 'react';
import * as style from '../../styles/organization.module.scss';

import GoogleMapReact from 'google-map-react';

function Map(props: any) {
  const geometryY = parseFloat(props.coordenates.props.ygriega) || -26.231718;
  const geometryX = parseFloat(props.coordenates.props.equis) || -63.2485696;
  const [zoom, setZoom] = useState(props.coordenates.props.zoom);
  useEffect(() => {
    setZoom(props.coordenates.props.zoom);
  }, [props.coordenates.props.zoom]);

  return (
    <div className={style.map}>
      <GoogleMapReact
        bootstrapURLKeys={{
          key: 'https://maps.googleapis.com/maps/api/js?v=3.exp&key=AIzaSyCDr1CIiG6Nop7lpjmIbVk8NVC1IjW_oXE',
        }}
        defaultCenter={{ lat: geometryY, lng: geometryX }}
        defaultZoom={zoom}
        options={{ styles: styles }}>
        <Marcador
          region={props.city}
          pais={props.country}
          lat={geometryY}
          lng={geometryX}
          name='My Marker'
          color='blue'
        />
      </GoogleMapReact>
    </div>
  );
}

export default Map;

export function Marcador(props: any) {
  const { color, name } = props;

  return (
    <>
      <div className={style.marker} style={{ backgroundColor: color, cursor: 'pointer' }} title={name} />
      <div className={style.mapInfo}>
        <div className={style.marker}>
          <div className={style.markerFill}></div>
          <div className={style.markerFillFill}></div>
        </div>
        <div className={style.address}>
          <div className={style.textContainer}>
            <div className={style.textContainerCity}>{props.region ? props.region : 'Desde LATAM'}</div>
            <div className={style.textContainerCountry}>{props.pais ? props.pais : 'Hacia el mundo'}</div>
          </div>
        </div>
      </div>
    </>
  );
}

